mapper.select:
select

from aoa_note_list where note_id in
(select note_id from aoa_receiver_note where user_id=#{id})

  and type_id=#{tid}

order by

  type_id ${type}


  status_id ${status}


  create_time ${time}


  create_time DESC

select

from aoa_note_list where (status_id in (select status_id from aoa_status_list where status_name like #{key}) or
DATE_format(create_time,'%Y-%m-%d') like #{key} or type_id in (select type_id from aoa_type_list where type_name like #{key})
or title like #{key}) and note_id in
(select note_id from aoa_receiver_note where user_id=#{id})

  and type_id=#{tid}

order by

  type_id ${type}


  status_id ${status}


  create_time ${time}


  create_time DESC

select

from aoa_note_list where (status_id in (select status_id from aoa_status_list where status_name like #{key}) or
DATE_format(create_time,'%Y-%m-%d') like #{key} or type_id in (select type_id from aoa_type_list where type_name like #{key})
or title like #{key}) and note_id in
(select note_id from aoa_receiver_note where user_id=#{id}) and  catalog_id=#{cid}

  and type_id=#{tid}

order by

  type_id ${type}


  status_id ${status}


  create_time ${time}


  create_time DESC

select

from aoa_note_list where catalog_id=#{cid} and note_id in
(select note_id from aoa_receiver_note where user_id=#{id})

  and type_id=#{tid}

order by

  type_id ${type}


  status_id ${status}


  create_time ${time}


  create_time DESC
